import React from "react";
import ShimmerTable from "./ShimmerTable";
import { decrypt, encrypt, fetchWithEncryption } from "../helper/commonHelper";

const Table = ({ data }) => {
  const containerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    padding: "20px",
    boxSizing: "border-box",
  };

  const tableStyle = {
    width: "100%",
    maxWidth: "800px",
    borderCollapse: "collapse",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  };

  const thStyle = {
    backgroundColor: "#f4f4f4",
    color: "#333",
    padding: "12px 15px",
    textAlign: "center",
    borderBottom: "2px solid #ddd",
  };

  const tdStyle = {
    padding: "12px 15px",
    borderBottom: "1px solid #ddd",
  };

  const trStyle = {
    transition: "background-color 0.3s",
  };

  const trHoverStyle = {
    backgroundColor: "#f9f9f9",
  };

  const handleClick = async (name) => {
    const data = {
      name,
    };
    const payload = await encrypt(JSON.stringify(data));
    try {
      const response = await fetchWithEncryption("http://localhost:5000/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(payload),
      });

      const result = await response.json();
      const decodedData = await decrypt(result);
      console.log(decodedData);
      const parsedData = JSON.parse(decodedData);
      console.log("Response from server:", parsedData);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div style={containerStyle}>
      <table style={tableStyle}>
        <thead>
          <tr>
            <th style={thStyle}>Name</th>
            <th style={thStyle}>Age</th>
            <th style={thStyle}>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.length > 0 &&
            data.map((row, index) => (
              <tr
                key={index}
                style={trStyle}
                onMouseOver={(e) =>
                  (e.currentTarget.style.backgroundColor = "#f9f9f9")
                }
                onMouseOut={(e) =>
                  (e.currentTarget.style.backgroundColor = "transparent")
                }
              >
                <td style={tdStyle}>{row.name}</td>
                <td style={tdStyle}>{row.age}</td>
                <td style={tdStyle}>
                  <button onClick={() => handleClick(row.name)}>Act</button>
                </td>
              </tr>
            ))}

          {data.length == 0 &&
            Array.from({ length: 5 }).map((_, index) => (
              <ShimmerTable key={index} />
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
